import {createSlice} from "@reduxjs/toolkit";

const state = {
    lostItems: []
};

const slice = createSlice({
    name: "lostItem",
    initialState: state,
    reducers: {
        getLostItems(state,action){},
        getLostItemsSuccess(state,action){
            state.lostItems = action.payload;
        }
    }
})

export const {
    getLostItems,
    getLostItemsSuccess
} = slice.actions;
export default slice.reducer;
