import React, { useEffect, useState } from "react";
import { apiCall } from './utils/apiCall';
import './index.css';
import Rodal from "rodal";
import 'rodal/lib/rodal.css'
import { useSubscription } from "react-stomp-hooks";

function Home() {

    const [posts, setPosts] = useState([]);
    const [users, setUsers] = useState([]);
    const [select, setSelect] = useState('All');
    const [currentUser, setCurrentUser] = useState({});

    useEffect(() => {

        return ()=>{
            setCurrentUserToOffline();
        }
    }, []);

    function getAllUsers(){
        apiCall('/user', 'get').then(res=>{
            setUsers(res.data)
        })
    }

    useSubscription("/topic", (msg)=>{
        getAllUsers()
        setUsers(JSON.parse(msg.body));
        getCurrentUser();
    })

    function getCurrentUser(){
        apiCall('/user/online/'+localStorage.getItem("userId"), "get").then(res=>{
            console.log(res.data);
        })
    }

    function setCurrentUserToOffline(){
        apiCall('/user/offline/'+localStorage.getItem("userId"), "get").then(res=>{
            console.log(res.data);
        })
    }





    return (
        <div className="row">
            {
                users.map(user=> <div>
                    <p>{user.name}</p>
                    <p>{user.email}</p>
                    <p>{user.online ? "online" : "offline"}</p>
                </div>)
            }
        </div>
    )
}

export default Home;
