import React, { useEffect, useState } from 'react';
import {Routes, Route, useNavigate, useLocation} from 'react-router-dom'
import Login from './pages/Login';
import Register from './pages/Register';
import Home from './pages/Home';
import { apiCall, getAccessToken } from './utils/apiCall';
import { useDispatch, useSelector } from 'react-redux';
import {getCurrentUser} from './store/slice/userReducer'
import Adminpage from './pages/Adminpage';


export default function App() {
    const {currentUser} = useSelector(state => state.userReducer);
    const dispatch = useDispatch();
    const navigate = useNavigate();
    const location = useLocation();
    const pages = [
        {
            url: "/admin-page",
            role: 'ROLE_SUPER_ADMIN'
        }
    ]

    useEffect(()=>{
       checkPermit();
       getAccessToken();
       dispatch(getCurrentUser())
    }, [location.pathname])

    function checkPermit(){
        for(let i = 0; i < pages.length; i++){
            if(location.pathname === pages[i].url){
                for(let j = 0; j < currentUser.roles.length; j++){
                    if(pages[i].role === currentUser.roles[j]){
                        return true;
                    }
                }
            }
        }
        return false;
    }
    
    
   
    return (
        <div>
            <Routes>
                <Route path='/' element={<Home/>} />
                <Route path='/register' element={<Register/>} />
                <Route path='/login' element={<Login/>} />
                <Route path='/admin-page' element={<Adminpage/>} />
            </Routes>
        </div>
    );
}
