import React, { useEffect, useState } from "react";
import { apiCall } from '../utils/apiCall';
import Rodal from "rodal";
import 'rodal/lib/rodal.css'
import { useSubscription } from "react-stomp-hooks";
import { Link, Outlet, useNavigate } from "react-router-dom";

function Drafts(){

    const [myMessages, setMyMessages] = useState([]);
    const [message,setMessage] = useState({});
    
    useEffect(()=>{
        getAllMyMessages();
    }, [])

    function getAllMyMessages(){
        apiCall('/message/draft/'+localStorage.getItem("userId"), 'get').then(res=>{
            console.log(res.data);
            setMyMessages(res.data)
        })
    }

    function back(){
        setMessage({});
    }

    function getMessage(id, index){
        setMessage({...myMessages[index]})
    }

    return (
        <div>
            {
                message.id ? 
                <div className="card col-4">
                    <div className="card-header">
                        <button onClick={back} className="btn btn-info m-2">back</button>
                        <h6>{message.title}</h6>
                    </div>
                    <div className="card-body">
                        <p>{message.text}</p>
                    </div>
                    <div className="card-footer">
                        <p>{message.firstName + " " + message.lastName}</p>
                        <button className="btn btn-warning m-2"> send</button>
                    </div>
                </div>
                : <table className="table">
                <thead>
                    <tr>
                        <th>title</th>
                        <th>text</th>
                        <th>fullName</th>
                        <th>updated At</th>
                    </tr>
                </thead>
                <tbody>
                    {
                        myMessages.map((message, index)=> <tr onClick={()=>getMessage(message.id, index)}>
                            <td>{message.title}</td>
                            <td>{message.text}</td>
                            <td>{message.firstName}</td>
                            <td>{message.updatedAt.slice(0, 10)}</td>
                        </tr>)
                    }
                </tbody>
            </table>
            }
        </div>
    )
}

export default Drafts;
