import React, { useEffect, useState } from 'react';
import {apiCall} from './utils/apiCall'


function App(){

    const [todos, setTodos] = useState([]);
    const [ids, setIds] = useState([]);
    const [checked, setChecked] = useState(false);
    const [status, setStatus] = useState([]);


    useEffect(()=>{
        getTodos();
    }, [])

    function getTodos(){
        apiCall('/todo', 'get').then(res=>{
            setTodos(res.data)
            generate(res.data)
            console.log(res.data);
        })
    }

    function changeStatus(id, index){
        console.log(status);
        status[index] = !status[index]
        setStatus([...status])
        if(status[index]){
            ids.push(id)
            setIds([...ids])
        }
        console.log(ids);
    }

    function submit(){
        apiCall('/todo/delete', 'delete', ids).then(res=>{
            getTodos();
        })
    }

    function changeStatusById(){
        apiCall('/todo/status-all', 'put', ids).then(res=>{
            getTodos();
        })
    }

    function update(id){
        apiCall('/todo/status/'+id, 'put', ids).then(res=>{
            getTodos();
        })
    }

    function generate(todoss){
        todoss.map(todo=>{
            status.push(false);
        })
    }


    return (
        <div>
            <div>
                <button onClick={submit} className='btn btn-danger'>Clear Todos</button>
                <button onClick={changeStatusById} className='btn btn-success mx-2'>Mark as Completed</button>
            </div>
            <table className="table">
                <thead>
                    <th></th>
                    <th>name</th>
                    <th>description</th>
                    <th>status</th>
                    <th>actions</th>
                </thead>
                <tbody>
                    {
                        todos.map((todo, index)=> <tr>
                            <td>
                                <input type="checkbox" onChange={()=>changeStatus(todo.id, index)} checked={status[index]} />
                            </td>
                            <td>{todo.name}</td>
                            <td>{todo.description}</td>
                            <td>
                                {
                                    todo.status ? <button  className='btn btn-success'>Completed</button> : <button className='btn btn-danger' onClick={()=>update(todo.id)}>Pending</button>
                                }
                            </td>
                            <td>
                                <button className='btn btn-success mx-1'>edit</button>
                                <button className='btn btn-danger mx-1'>delete</button>
                            </td>
                        </tr>)
                    }
                </tbody>
            </table>
        </div>
    )
}

export default App;
