import React, { useEffect, useState } from "react";
import {apiCall} from './utils/apiCall';
import Rodal from "rodal";
import 'rodal/lib/rodal.css'

function App(){

    const [tasks, setTasks] = useState([]);
    const [categories, setCategories] = useState([]);
    const [visible, setVisible] = useState(false);

    useEffect(()=>{
        getCategories();
        getTasks();
    }, [])

    function getCategories(){
        apiCall('/category', 'get').then(res=>{
            setCategories(res.data)
            console.log(res.data);
        })
    }

    function toggle(){
        setVisible(prev=>!prev)
    }

    function getTasksById(id){
        apiCall('/task/'+id, 'get').then(res=>{
            console.log(res.data);
            setTasks(res.data)
        })
    }

    function getTasks(id){
        apiCall('/task', 'get').then(res=>{
            console.log(res.data);
            setTasks(res.data)
        })
    }

    function submit(e){
        e.preventDefault();
        apiCall("/category", 'post', {name: e.target[0].value}).then(res=>{
            getCategories();
        })
    }

    return (
        <div className="container">
            <div className="row">
                <div className="sider col-3">
                    <button onClick={toggle} className="btn btn-warning w-100 my-2">add Category</button>
                    {
                        categories.map(category=> <div>
                            <p>{category.name}</p>
                        </div>)
                    }
                </div>
            </div>

            <Rodal visible={visible} onClose={toggle} >
                <form onSubmit={submit} action="">
                    <input type="text" placeholder="Name..." className="form-control" />
                    <button className="btn btn-warning w-100 my-2">submit</button>
                </form>
            </Rodal>
        </div>
    )
}

export default App;
