import React, { useEffect, useState } from 'react';
import Rodal from 'rodal';
import 'rodal/lib/rodal.css'

export default function App() {
    
    const [cryptos, setCryptos] = useState([]);
    const [visible, setVisible] = useState(false);

    useEffect(()=>{
        localStorage.setItem("cryptos", JSON.stringify([
            {coinimg: 'https://assets.coingecko.com/coins/images/1/large/bitcoin.png?1696501400', coinprice: 64500},
            {coinimg: 'https://assets.coingecko.com/coins/images/5/large/dogecoin.png?1696501409', coinprice: 0.153186}
        ]))
    }, [])
    
    function toggle(){
        setCryptos(JSON.parse(localStorage.getItem("cryptos")));
        setVisible(prev=>!prev)
    }

    function deleteCrypto(i){
        console.log(i);
        let a = cryptos.filter((item, index)=>index !==i);
        localStorage.setItem(cryptos, JSON.stringify(a))
        setCryptos(JSON.parse(localStorage.getItem("cryptos")))
    }
   
    return (
        <>
            <div className="header-top">
                <button onClick={toggle} className='btn btn-warning'>Ok</button>
                <Rodal visible={visible} onClose={toggle}>
                    {
                        cryptos.map((crypto, index)=> <div>
                            <p>{crypto.coinprice}</p>
                            <button onClick={()=>deleteCrypto(index)} className='btn btn-info'>delete</button>
                        </div>)
                    }
                </Rodal>
            </div>
        </>
    );
}
