import React, { useEffect, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { setOrderProduct } from '../store/slice/userReducer';

function OrderProduct() {

    const {orderProducts} = useSelector(state => state.userReducer);
    const dispatch = useDispatch();
    const [arr, setArr] = useState([{count:0}]);

    useEffect(()=>{
        setArr([])
    }, [])

    function increment( index){
        arr[0].count ++;
        
        setArr([...arr]);
        // dispatch(setOrderProduct(arr));
    }

    function decrement( index){
        
        arr[index].count -= 1;
        setArr([...arr])
        dispatch(setOrderProduct(arr));
    }

    function deleteOrder(order, index){
        
    }

    return (
        <div>
            <table className='table'>
                <thead>
                    <tr>
                        <th>t/r</th>
                        <th>name</th>
                        <th>amount</th>
                        <th>price</th>
                        <th>actions</th>
                    </tr>
                </thead>
                <tbody>
                    {
                        orderProducts.map((order, index)=> <tr key={order.id}>
                            <td>{order.id}</td>
                            <td>{order.name}</td>
                            <td>{order.amount}</td>
                            <td><button onClick={()=> increment (index)} className='btn btn-warning mx-1'>+</button>{order.count} <button onClick={()=> decrement(order, index)} className='btn btn-warning mx-1'>-</button> </td>
                            <td>
                                <button onClick={()=> deleteOrder(index)} className='btn btn-danger'>delete</button>
                            </td>
                        </tr>)
                    }
                </tbody>
            </table>
        </div>
    );
}

export default OrderProduct;
