import React, { useEffect, useState } from "react";
import { apiCall } from './utils/apiCall';
import { useLocation } from "react-router-dom";

function Todos(){
    const [todos, setTodos] = useState({});
    const location = useLocation();
    useEffect(() => {
        getTodos(1);
        console.log(todos);
    }, [])
    function getTodos(page) {
        apiCall('/todo/' + location.pathname.slice(6) + "?limit=5&page="+page, 'get').then(res => {
            setTodos(res.data)
            console.log(res.data.data);
        })
    }

    function search(page){
        let search = document.getElementById('search');
        apiCall('/todo/search/' + location.pathname.slice(6) + "?limit=5&search="+search.value+"&page="+page, 'get').then(res=>{
            setTodos(res.data)
        })
    }

    function generateBtn() {
        let a = todos.pageCount;
        let b = [];
        for (let i = 1; i <= a; i++) {
            b.push(i);
        }
        return (
            <div>
                {
                    b.map(btn => <button className="btn btn-info m-2" onClick={()=>getTodos(btn)}>{btn}</button>)
                }
            </div>
        )
    }


    return (
        <div className="container">
            <div className="row">
                <div className="col-12">
                    <h1 className="text-center">Todos</h1>
                    <div>
                    <input type="text" className="form-control w-25 my-2" id="search" placeholder="Search..." />
                    <button onClick={search} className="btn btn-warning m-2">send</button>
                    </div>
                </div>
                <table className="table">
                    <thead>
                        <tr>
                            <th>t/r</th>
                            <th>title</th>
                            <th>body</th>
                        </tr>
                    </thead>
                    <tbody>
                        {
                            todos.data ? todos.data.map(todo => <tr>
                                <td>{todo.id}</td>
                                <td>{todo.title}</td>
                                <td>
                                    <input type="checkbox" checked={todo.completed} />
                                </td>
                            </tr>) : ""
                        }
                    </tbody>
                </table>
                <div className="col-12">
                    {
                        generateBtn()
                    }
                </div>
            </div>
        </div>
    )
}

export default Todos;
