import React, { useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { getUserChats } from '../store/slice/chatReducer';
import { searchUser, setSelectedUser } from '../store/slice/userReducer';

function Sider() {
    const {chats} = useSelector(state => state.chatReducer);
    const {currentUser, searchingUser} = useSelector(state => state.userReducer);
    const dispatch = useDispatch();


    useEffect(()=>{
        if(currentUser.id){
            dispatch(getUserChats(currentUser.id))
            console.log(chats);
        }
    }, [])


    function search(){
        const value = document.getElementById("search").value;
        dispatch(searchUser(value));
    }

    return (
        <div className="col-3">
            <div>
                <input type="text" id='search' />
                <button onClick={search} className='btn btn-warning'>search</button>
                <div onClick={()=>dispatch(setSelectedUser(searchingUser))}>
                    <h5>{searchingUser.fullName}</h5>
                    <p>{searchingUser.phoneNumber}</p>
                </div>
            </div>
        </div>
    );
}

export default Sider;
