import React from 'react';
import { apiCall } from '../utils/apiCall'
import { useNavigate } from 'react-router-dom';

function Register() {

    const navigate = useNavigate();

    function submit(e) {
        e.preventDefault();
        let data = {
            firstName: e.target[0].value,
            lastName: e.target[1].value,
            username: e.target[1].value,
            password: e.target[3].value,
            age: e.target[4].value,
            passport: e.target[5].value,
            phoneNumber: e.target[6].value
        };
        apiCall('/auth/register', 'post', data).then(res => {
                navigate('/login')
                alert("Success")
                e.target[0].value = '';
                e.target[1].value = '';
                e.target[1].value = '';
                e.target[3].value = '';
                e.target[4].value = '';
                e.target[5].value = '';
                e.target[6].value = '';
                e.target[1].value = res.data
                e.target[1].style.color = 'red'
        }).catch(err=>{
            alert("Fail!")
        })
    }

    return (
        <div className='container'>
            <div className="row d-flex justify-content-center">
                <div className="card col-5 my-3">
                    <form onSubmit={submit} action="">
                        <div className="card-header">
                            <h5 className='text-center'>Register</h5>
                        </div>
                        <div className="card-body">
                            <input type="text" className='form-control my-2' placeholder='Firstname...' />
                            <input type="text" className='form-control my-2' placeholder='Lastname...' />
                            <input type="text" className='form-control my-2' placeholder='Username...' />
                            <input type="text" className='form-control my-2' placeholder='Password...' />
                            <input type="text" className='form-control my-2' placeholder='Age...' />
                            <input type="text" className='form-control my-2' placeholder='Passport Number...' />
                            <input type="text" className='form-control my-2' placeholder='Phone Number...' />
                        </div>
                        <div className="card-footer">
                            <button className='btn btn-warning w-100'>submit</button>
                            <a href="http://localhost:3000/login">have you an account?</a>
                        </div>
                    </form>
                </div>
            </div>
        </div>
    );
}

export default Register;
