import React, { useEffect, useState } from 'react';
import Rodal from 'rodal';
import 'rodal/lib/rodal.css'
import './index.css'
import { useDispatch, useSelector } from 'react-redux';
import {changeLanguage, getClasses, getLessons, getSciences} from '../store/slice/userReducer'
import Header from './Header';
import { useNavigate } from 'react-router-dom';

function Home() {

    const {lang, classes} = useSelector(state=>state.userReducer);
    const dispatch = useDispatch();
    const navigate = useNavigate();

    useEffect(() => {
        console.log("classNumbers");
        dispatch(getClasses());
    }, [])




    return (
        <div>
            <Header/>
            <div className="register">

            </div>
            <div className="body">
                <div className='desciption'>
                    <h1 className='text-center'>{lang === 'uz' ? "Videodarsliklar kutubxonasi" : lang === 'eng' ? "Library of video tutorials" : ""}</h1>
                    <p className='text-center'>{lang === 'uz' ? "Elektron videodarsliklarning eng katta to'plami" : lang === 'eng' ? "The largest collection of electronic video tutorials" : ""}</p>
                </div>
                <div className='body-wrapper'>
                    {
                        classes.map(clas => <div onClick={()=>{dispatch(getSciences(clas.id)); navigate('/lesson')}} className='classes'>
                            <div className="card-header">
                                <h2>{lang==='uz' ? clas.className : lang === 'eng' ? clas.className[0] + " class" : "" }</h2>
                            </div>
                        </div>)
                    }
                    <div className='classes'>
                        <div className="card-header">
                            <h3>{lang === 'uz' ? "Qoshimcha fanlar" : lang === 'eng' ? "Additional subjects" : ""}</h3>
                        </div>
                    </div>
                    <div className='classes'>
                        <div className="card-header">
                            <h4 className='text-center'>{lang === 'uz' ? "STEAM yo'nalishi bo'yicha video materiallar" : lang === 'eng' ? "Video materials in the direction of STEAM" : ""}</h4>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    )
}

export default Home;
