import React, { useEffect, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { getUsers, toggle, setStatus, setCurrentUser, addLoan } from "../store/slice/userReducer";
import {addPayment} from '../store/slice/paymentReducer'
import Rodal from 'rodal';
import 'rodal/lib/rodal.css'
import { useNavigate } from 'react-router-dom';
import { useForm } from 'react-hook-form';

function Loan() {

    const { users, visible, currentUser } = useSelector(state => state.userReducer);
    const [status, setStatus] = useState("");
    const dispatch = useDispatch();
    const navigate = useNavigate();
    const {handleSubmit, reset, register} = useForm();

    useEffect(() => {
        dispatch(getUsers())
    }, [currentUser.loanAmount])


    function submit(data){
        if(status==="TOLASH"){
            dispatch(addPayment({data: {userId: currentUser, amount: data.amount}}))
        }else{
            dispatch(addLoan({data: {userId: currentUser.id, amount: data.amount}}))
        }
        dispatch(toggle())
        reset({amount: ""})
    }

    return (
        <div>

            <table className='table'>
                <thead>
                    <tr>
                        <th>T/r</th>
                        <th>full name</th>
                        <th>phone number</th>
                        <th>loan amunt</th>
                        <th>actions</th>
                    </tr>
                </thead>
                <tbody>
                    {
                        users.map((user, index) => <tr key={user.id}>
                            <td>{index+1}</td>
                            <td>{user.firstname + " " + user.lastname}</td>
                            <td>{user.phoneNumber}</td>
                            <td>{user.loanAmount}</td>
                            <td>
                                <button onClick={()=> {setStatus("QARZ_OLISH"); dispatch(toggle()); dispatch(setCurrentUser(user)); }} className='btn btn-success mx-1'>+</button>
                                <button onClick={()=> {setStatus("TOLASH"); dispatch(toggle()); dispatch(setCurrentUser(user))}} className='btn btn-success mx-1'>-</button>
                                <button onClick={()=> navigate("/payment")} className='btn btn-success mx-1'>history</button>
                            </td>
                        </tr>)
                    }
                </tbody>
            </table>
                {
            console.log(status)

                }
            <Rodal height={190} visible={visible} onClose={()=>dispatch(toggle())}>
                <form onSubmit={handleSubmit((data)=> submit(data))} className='mt-3'>
                    {status === "TOLASH" ? "To'lov summasi" : status === "QARZ_OLISH" ? "Qarz miqdorini kiriting!" : ""}
                    <input {...register("amount")} className='form-control my-2' type="text" />
                    <button type='submit' className='btn btn-warning w-100'>send</button>
                </form>
            </Rodal>
        </div>
    );
}

export default Loan;
