import {apiCall} from '../utils/apiCall'
import React, { useEffect, useState } from 'react';

function Home() {

    const [tasks, setTasks] = useState([])
    const [ids, setIds] = useState([]);

    useEffect(()=>{
        getTasks();
    }, [])

    function getTasks(){
        apiCall('/task', 'get').then(res=>{
            console.log(res.data);
            setTasks(res.data)
        })
    }

    function selectTask(id, e){
        if(e.target.checked){
            ids.push(id);
        }else{
            ids = ids.filter((item)=> item!==id);
        }
        setIds([...ids])
        console.log(ids);
    }

    function changeColor(status){
        return status === "NON_STARTED" ?
                "#EF8A0E" : 
                status === "ON_HOLD" ? 
                "#13B8F4" :
                status === "IN_PROGRESS" ?
                "#74BC40" :
                status === "CLOSED" ? 
                "#B9333F" :
                status === "COMPLETED" ?
                "#5DDCA4" : "white"
    }

    return (
        <div className='container'>
            <table className='table'>
                <thead>
                    <tr>
                        <th>t/r</th>
                        <th>name</th>
                        <th>status</th>
                        <th>username</th>
                        <th>select</th>
                    </tr>
                </thead>
                <tbody>
                    {
                        tasks.map(task=> <tr>
                            <td>{task.id}</td>
                            <td>{task.name}</td>
                            <td style={{background: changeColor(task.status)}}>{task.status}</td>
                            <td>{task.username}</td>
                            <td>
                                <input onChange={(e)=>selectTask(task.id, e)} type="checkbox" />
                            </td>
                        </tr>)
                    }
                </tbody>
            </table>
        </div>
    );
}

export default Home;
