import React, { useEffect, useState } from "react";
import { apiCall } from './utils/apiCall';
import './index.css';
import Rodal from "rodal";
import 'rodal/lib/rodal.css'
import {useSubscription} from "react-stomp-hooks";

function Home() {

    const [posts, setPosts] = useState([]);
    const [users, setUsers] = useState([]);
    const [select, setSelect] = useState('All');
    const [currentUser, setCurrentUser] = useState({});

    useEffect(() => {
        getPosts(0, 1);
        getUsers();
        getUserById();
    }, []);

    useSubscription("/topic/post", (msg)=>{
        console.log('nma gap ', msg.body);
        posts.push(JSON.parse(msg.body));
        setPosts([...posts]);
    })

    function getPosts(id, page) {
        let url = id <= 0 || id === 'All' ? '' : '/' + id;
        apiCall('/post' + url + '?pageNumber=' + page + '&limit=5', 'get').then(res => {
            setPosts(res.data)
        })
    }

    function changeSelect(e) {
        setSelect(e.target.value)
        if (e.target.value !== 'All') {
            getPosts(e.target.value, 1)
        } else {
            getPosts(0, 1)
        }
    }

    function getUserById(){
        apiCall('/user/'+localStorage.getItem("userId"), 'get').then(res=>{
            setCurrentUser(res.data);
        })
    }

    function getUsers() {
        apiCall('/user', 'get').then(res => {
            setUsers(res.data);
        })
    }

    function paginate(number) {
        getPosts(select, number);
    }

    function generateBtn() {
        let a = posts[0]?.pageCount;
        let b = [];
        for (let i = 1; i <= a; i++) {
            b.push(i);
        }
        return (
            <div>
                {
                    b.map(btn => <button className="btn btn-info m-2" onClick={() => paginate(btn)}>{btn}</button>)
                }
            </div>
        )
    }

    function addPost(e){
        e.preventDefault();
        let data = {title: e.target[0].value, description: e.target[1].value, userId: e.target[2].value};
        console.log(data);
        apiCall('/post', 'post', data).then(res=>{
            getPosts(select, 1);
            e.target[0].value = ''
            e.target[1].value = ''
        })
    }


    return (
        <div className="row">
            <div className="col-4">
                <select className="form-control my-2" onChange={(e) => changeSelect(e)} name="" id="select">
                    <option value="All">All</option>
                    {
                        users.map(user => <option value={user.id}>{user.fullName}</option>)
                    }
                </select>

                <div className="card">
                    <form onSubmit={addPost} action="">
                        <div className="card-header">
                            <h6 className="text-center">Add post</h6>
                        </div>
                        <div className="card-body">
                            <input type="text" className="form-control my-2" placeholder="Title..." />
                            <input type="text" className="form-control my-2" placeholder="Description..." />
                            <select className="form-control my-2" name="" id="select">
                                {
                                    users.map(user => <option value={user.id}>{user.fullName}</option>)
                                }
                            </select>
                        </div>
                        <div className="card-footer">
                            <button className="btn btn-warning my-2 w-100">Submit</button>
                        </div>
                    </form>
                </div>
                <div className="card my-2">
                    <div className="card-header">
                        <p>{currentUser?.fullName}</p>
                        <p>@{currentUser?.email}</p>
                    </div>
                </div>
            </div>
            <div className="col-8 ">
                <h3>Posts</h3>
                <div className="content">
                    {
                        posts.map(post => <div className="card col-4">
                            <div className="card-header">
                                <h6>{post.id}) {post.title}</h6>
                            </div>
                            <div className="card-body">
                                <p>{post.description}</p>
                                <p>{post.fullName}</p>
                            </div>
                        </div>)
                    }
                </div>
                {generateBtn()}
            </div>


        </div>
    )
}

export default Home;
