import React, { useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { searchUserByPhoneNumber } from '../store/slice/userReducer';
import { getCurrentChat } from '../store/slice/chatReducer';

function Home() {
    const [openChat, setOpenChat] = useState(false)
    const dispatch = useDispatch();
    const {searchUser, currentUser} = useSelector(state => state.userReducer);
    const {currentChat} = useSelector(state => state.chatReducer);
    function search(){
        let value = document.getElementById("search").value;
        dispatch(searchUserByPhoneNumber(value));
    }

    function sendMessage(){
        let value = document.getElementById("send").value;
    }

    function goToChat(){
        dispatch(getCurrentChat({initiatorId: currentUser.id, participantId: searchUser.id}))
        setOpenChat(true)
    }

    return (
        <div className='row'>
            <div className="sider col-3 mr-1">
                <div>
                    <input type="text" id='search' />
                    <button onClick={search} className='btn btn-warning'>search</button>
                </div>
                <div>
                    {
                        searchUser.id ? <div>
                        <h5>{searchUser.fullName}</h5>
                        <p>{searchUser.phoneNumber}</p>
                        <button onClick={()=>goToChat(searchUser.id)}>go to chat</button>
                    </div> : ""
                    }
                </div>
            </div>
            <div className='col-8'>
                <div className="header">
                    <h1>Header</h1>
                </div>
                <div style={{height: "400px"}}>
                    {

                    }
                </div>
                {
                    openChat ? <div className="footer">
                    <input type="text" id='send' />
                    <button className='btn btn-success' onClick={sendMessage}>send message</button>
                </div> : ""
                }
            </div>
        </div>
    );
}

export default Home;
