import React, { useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { getUsers, toggle, setStatus } from "../store/slice/userReducer"
import Rodal from 'rodal';
import 'rodal/lib/rodal.css'
import { useNavigate } from 'react-router-dom';

function Loan() {

    const { users, visible, status } = useSelector(state => state.userReducer);
    const dispatch = useDispatch();
    const navigate = useNavigate();

    useEffect(() => {
        dispatch(getUsers())
    }, [])


    return (
        <div>

            <table className='table'>
                <thead>
                    <tr>
                        <th>T/r</th>
                        <th>full name</th>
                        <th>phone number</th>
                        <th>loan amunt</th>
                        <th>actions</th>
                    </tr>
                </thead>
                <tbody>
                    {
                        users.map((user, index) => <tr key={user.id}>
                            <td>{index+1}</td>
                            <td>{user.firstname + " " + user.lastname}</td>
                            <td>{user.phoneNumber}</td>
                            <td>{user.loanAmount}</td>
                            <td>
                                <button onClick={()=> {dispatch(setStatus("QARZ_OLISH")); dispatch(toggle())}} className='btn btn-success mx-1'>+</button>
                                <button onClick={()=> {dispatch(setStatus("TOLASH")); dispatch(toggle())}} className='btn btn-success mx-1'>-</button>
                                <button onClick={()=> navigate("/payment")} className='btn btn-success mx-1'>history</button>
                            </td>
                        </tr>)
                    }
                </tbody>
            </table>

            <Rodal height={190} visible={visible} onClose={()=>dispatch(toggle())}>
                <form className='mt-3'>
                    {status == "TOLASH" ? "To'lov summasi" : status == "QARZ_OLISH" ? "Qarz miqdorini kiriting!" : ""}
                    <input className='form-control my-2' type="text" />
                    <button className='btn btn-warning w-100'>send</button>
                </form>
            </Rodal>
        </div>
    );
}

export default Loan;
