import {apiCall} from '../utils/apiCall'
import React, { useEffect, useState } from 'react';

function Home() {

    const [task, setTask] = useState([])

    useEffect(()=>{
        getTasks();
    }, [])

    function getTasks(){
        apiCall('/task', 'get').then(res=>{
            console.log(res.data);
        })
    }

    function changeColor(status){
        return status == "NON_STARTED" ?
                "#EF8A0E" : 
                status == "ON_HOLD" ? 
                "#13B8F4" :
                status == "IN_PROGRESS" ?
                "#74BC40" :
                status == "CLOSED" ? 
                "#B9333F" :
                status == "COMPLETED" ?
                "#5DDCA4" : "white"
    }

    return (
        <div>
            
        </div>
    );
}

export default Home;
