import React, { useEffect, useState } from 'react';
import {apiCall} from '../utils/apiCall';

function Teacher() {

    const [groups, setGroups] = useState([]);
    const [group, setGroup] = useState([]);

    useEffect(()=>{
        getGroups();
        getTimeTables();
        getStudentTimeTables();
        getTimeTableDays();
    }, [])

    function getGroups(){
        apiCall('/group/teacher', 'get').then(res=>{
            setGroups(res.data)
            console.log(res.data);
        })        
    }

    function getGroup(id = 1){
        apiCall('/group/'+id, 'get').then(res=>{
            setGroup(res.data)
            console.log(res.data);
        })
    }

    function getTimeTables(groupId=1){
        apiCall('/time/'+groupId, 'get').then(res=>{
            console.log(res.data);
        })
    }

    function getStudentTimeTables(timeTableId=2){
        apiCall('/student-time/'+timeTableId, 'get').then(res=>{
            console.log(res.data);
        })
    }

    function getTimeTableDays(studentTimeTableId=6){
        apiCall('/day/'+studentTimeTableId, 'get').then(res=>{
            console.log(res.data);
        })
    }

    return (
        <div className='container'>
            <div className="row">
                <div className="col-12">
                    <h4 className='text-center'>Dashboard</h4>
                </div>
                <div className="col-1">
                    {
                        groups.map(group=> <div onClick={()=>getGroup(group.id)} className='card my-1'>
                            <div className="card-header">
                                <p>{group.name}</p>
                            </div>
                        </div>)
                    }
                    <div className="card">
                        <div className="card-header">
                            <p className='text-center'><i className='bi bi-gear'></i></p>
                        </div>
                    </div>
                </div>
                <div className="col-4">
                    {
                        group.name
                    }
                </div>
            </div>
            
        </div>
    );
}

export default Teacher;
