import React, { useEffect, useState} from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { addPost, deletePost, getPosts, toggle } from '../store/slice/PostReducer';
import Rodal from 'rodal';
import 'rodal/lib/rodal.css'
import { useForm } from 'react-hook-form';

function Posts() {  

    const { posts, visible } = useSelector(state => state.postReducer);
    const { currentUserId } = useSelector(state => state.userReducer);
    const dispatch = useDispatch();
    const {register, handleSubmit, reset} = useForm();
    const [postId, setPostId] = useState("");

    useEffect(() => {
        dispatch(getPosts(currentUserId));
        reset({title: "salom", body: "alik"})
    }, [currentUserId])

    return (
        <div >
            <div className='row'>
                {
                    posts.map((post, index) => <div className='card col-3 my-1 mx-4' key={post.id}>
                        <div className="card-header">
                            <h5 className='text-center'>{post.title}</h5>
                        </div>
                        <div className="card-body">
                            {post.body}
                        </div>
                        <div className="card-footer">
                            <button onClick={()=> {setPostId(post.id); reset({title: post.title, body: post.body})}} className="btn btn-success mx-1">edit</button>
                            <button onClick={()=>dispatch(deletePost({userId: post.userId, postId: post.id}))} className="btn btn-danger mx-1">delete</button>
                        </div>
                    </div>)
                }
            </div>
            <div>
                <button onClick={()=>dispatch(toggle())} className='btn btn-warning mx-3 my-4'>add +</button>
            </div>

            <Rodal visible={visible} onClose={()=>dispatch(toggle())}>
                <form onSubmit={handleSubmit((data)=>{dispatch(addPost({data: {...data, userId: currentUserId, id: postId}, reset})); dispatch(toggle()); reset(); setPostId("")})} className='my-3'>
                    Title
                    <input {...register("title")} type="text" className="form-control" />
                    Body
                    <input {...register("body")} type="text" className="form-control" />
                    <button type='submit' className="btn btn-success my-4 w-100">submit</button>
                </form>
            </Rodal>
        </div>
    );
}

export default Posts;
