import {all} from "redux-saga/effects"
import {userSaga} from "./userSaga";
import { postSaga } from "./loanSaga";
import { todoSaga } from "./paymentSaga";


export default function* rootSaga() {
    yield all(
        [
            userSaga(),
            loanSaga(),
            todoSaga()
        ]
    )
}
