import { apiCall, getAccessToken } from '../utils/apiCall'
import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import Rodal from 'rodal';
import 'rodal/lib/rodal.css'
import './index.css'
import logo from '../resources/images/msg5364238833-27433.png.jpg'
import { useDispatch, useSelector } from 'react-redux';
import {changeLanguage} from '../store/slice/userReducer'

function Home() {

    const [classes, setClasses] = useState([]);
    const {lang} = useSelector(state=>state.userReducer);
    const dispatch = useDispatch();

    useEffect(() => {
        getClasses();
        getSciences();
        getLessons();
    }, [])

    function getClasses() {
        apiCall('/lesson', 'get').then(res => {
            setClasses(res.data)
            console.log(res.data);
        })
    }

    function getSciences(classNumberId = 6) {
        apiCall('/lesson/science/' + classNumberId, 'get').then(res => {
            console.log(res.data);
        })
    }

    function getLessons(scienceId = 5) {
        apiCall("/lesson/lessons/" + scienceId, 'get').then(res => {
            console.log(res.data);

        })
    }

    function changeLang(e){
        dispatch(changeLanguage(e.target.value))
    }

    return (
        <div>
            <div className="header">
                <div className="logo">
                    <img src={logo} alt="" />
                </div>
                <div className="navbar">
                    <div className='choose-lang'>
                        <select onChange={changeLang} className='form-control' name="" id="">
                            <option value="uz">O'zbek</option>
                            <option value="eng">English</option>
                        </select>
                    </div>
                    <div className="serach">
                        <input type="text" className='form-control' placeholder=' Qidiruv' />
                    </div>
                    <div className='themes'>
                        <i className='bi bi-eye-fill'></i>
                    </div>
                    <div className="sport">
                        <p><strong>Sport qo'llanma</strong></p>
                    </div>
                    <div className="lessons">
                        <p><strong>Darslar</strong></p>
                    </div>
                    <div className="help">
                        <p><strong>Yordam</strong></p>
                    </div>
                </div>
            </div>
            <div className="register">

            </div>
            <div className="body">
                <div className='desciption'>
                    <h1 className='text-center'>{lang === 'uz' ? "Videodarsliklar kutubxonasi" : lang === 'eng' ? "Library of video tutorials" : ""}</h1>
                    <p className='text-center'>{lang === 'uz' ? "Elektron videodarsliklarning eng katta to'plami" : lang === 'eng' ? "The largest collection of electronic video tutorials" : ""}</p>
                </div>
                <div className='body-wrapper'>
                    {
                        classes.map(clas => <div className='classes'>
                            <div className="card-header">
                                <h2>{lang==='uz' ? clas.className : lang === 'eng' ? clas.className[0] + " class" : "" }</h2>
                            </div>
                        </div>)
                    }
                    <div className='classes'>
                        <div className="card-header">
                            <h3>{lang === 'uz' ? "Qoshimcha fanlar" : lang === 'eng' ? "Additional subjects" : ""}</h3>
                        </div>
                    </div>
                    <div className='classes'>
                        <div className="card-header">
                            <h4 className='text-center'>{lang === 'uz' ? "STEAM yo'nalishi bo'yicha video materiallar" : lang === 'eng' ? "Video materials in the direction of STEAM" : ""}</h4>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    )
}

export default Home;
