import React, { useEffect, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { getLostItems, getLostItemsSuccess } from '../store/slice/lostItemReducer';
import { apiCall } from '../utils/apiCall';
import Rodal from 'rodal';
import 'rodal/lib/rodal.css'
import { useNavigate } from 'react-router-dom';
import { getCurrentUser, getUserAdvertise } from '../store/slice/userReducer';
import Header from '../components/Header';
import './index.css'

function Main() {

    const { lostItems } = useSelector(state => state.lostItemReducer);
    const { userAdvertise, currentUser } = useSelector(state => state.userReducer);
    const dispatch = useDispatch();
    const [filter, setFilter] = useState({
        holat: '',
        type: '',
        subType: '',
        region: '',
        district: ''
    });
    const [types, setTypes] = useState([]);
    const [subTypes, setSubTypes] = useState([]);
    const [regions, setRegions] = useState([]);
    const [districts, setDistricts] = useState([]);
    const [visible, setVisible] = useState(false);
    const [aLostItem, setALostItem] = useState({});
    const navigate = useNavigate();

    useEffect(() => {
        dispatch(getLostItems(filter));
        getTypes();
        getRegions();
        dispatch(getCurrentUser());
        dispatch(getUserAdvertise());
    }, [])


    function getTypes() {
        apiCall('/api/type', 'get', null, {}).then(res => {
            setTypes(res.data)
        })
    }
    function getSubTypes(typeId) {
        if (typeId) {
            apiCall('/api/type/subtype/' + typeId, 'get', null, {}).then(res => {
                setSubTypes(res.data)
            })
        }
    }
    function getRegions() {
        apiCall('/api/type/region', 'get', null, {}).then(res => {
            setRegions(res.data)
        })
    }
    function getDistrict(regionId) {
        if (regionId) {
            apiCall('/api/type/district/' + regionId, 'get', null, {}).then(res => {
                setDistricts(res.data)
            })
        }
    }
    function changeType(e) {
        setFilter({ ...filter, type: e.target.value });
        getSubTypes(e.target.value)
        dispatch(getLostItems({ ...filter, type: e.target.value }));
    }
    function changeRegion(e) {
        setFilter({ ...filter, region: e.target.value });
        getDistrict(e.target.value);
        console.log(filter);
        dispatch(getLostItems({ ...filter, region: e.target.value }));
    }
    function toggle() {
        setVisible(prev => !prev);
    }
    function reviewLostItem(lostItem) {
        setALostItem(lostItem)
        toggle();
    }
    function changeOwner(id) {
        apiCall('/api/lost/' + id, 'put').then(res => {
            console.log(res.data);
        }).catch(err => {
            navigate('/login');
        })
    }

    function goToDashboard() {
        if (userAdvertise.length) {
            navigate('/dashboard');
        } else {
            alert("Sizda bildirishnomalar mavjud emas!");
        }
    }

    function search(e){
        if(e.keyCode===13){
            apiCall('/api/lost/search?registry='+e.target.value, 'get', null, null).then(res=>{
                dispatch(getLostItemsSuccess(res.data))
            })
        }
    }
    return (
        <div>
            <Header />
            {currentUser.id ? <button onClick={goToDashboard} className='btn btn-warning'>Bildirishnomalar {userAdvertise.length}</button> : ""}
            <div>
                <div className='filter'>
                    <div>
                        <select className='form-control' onChange={(e) => { setFilter({ ...filter, holat: e.target.value }); dispatch(getLostItems({ ...filter, holat: e.target.value })) }} name="" id="">
                            <option value="">Holatni tanlang</option>
                            <option value="YOQOTIB_QOYILGAN">YOQOTIB_QOYILGAN</option>
                            <option value="TOPIB_OLINGAN">TOPIB_OLINGAN</option>
                        </select>
                    </div>
                    <div>
                        <select className='form-control' onChange={(e) => changeType(e)} name="" id="">
                            <option value="">Ashyo turini tanlang</option>
                            {
                                types.map(type => <option key={type.id} value={type.id}>{type.name}</option>)
                            }
                        </select>
                    </div>
                    <div>
                        <select className='form-control' onChange={(e) => { setFilter({ ...filter, subType: e.target.value }); dispatch(getLostItems({ ...filter, subType: e.target.value })); }} name="" id="">
                            <option value="">Ashyo sub turini tanlang</option>
                            {
                                subTypes?.map(subtype => <option key={subtype.id} value={subtype.id}>{subtype.name}</option>)
                            }
                        </select>
                    </div>
                    <div>
                        <select className='form-control' onChange={(e) => changeRegion(e)} name="" id="">
                            <option value="">Hududni tanlang</option>
                            {
                                regions.map(region => <option key={region.id} value={region.id}>{region.name}</option>)
                            }
                        </select>
                    </div>
                    <div>
                        <select className='form-control' onChange={(e) => { setFilter({ ...filter, district: e.target.value }); dispatch(getLostItems({ ...filter, district: e.target.value })); }} name="" id="">
                            <option value="">Tumanni tanlang</option>
                            {
                                districts?.map(district => <option key={district.id} value={district.id}>{district.name}</option>)
                            }
                        </select>
                    </div>
                    <div>
                        <input type="text" onKeyUp={(e)=>search(e)} className='form-control' placeholder='ABC0000000000' />
                    </div>
                </div>
                <div className="body">
                    <table className='table'>
                        <thead>
                            <tr>
                                <th>t/r</th>
                                <th>Status</th>
                                <th>Buyum turi</th>
                                <th>Buyum sub turi</th>
                                <th>Viloyat</th>
                                <th>Tuman</th>
                                <th>Tashkilot</th>
                                <th>Desciption</th>
                                <th>Sana</th>
                                <th>Reyestrdagi raqami</th>
                                <th>actions</th>
                            </tr>
                        </thead>
                        <tbody>
                            {
                                lostItems.map(lostItem => <tr key={lostItem.id}>
                                    <td>{lostItem.id}</td>
                                    <td>{lostItem.status}</td>
                                    <td>{lostItem.type}</td>
                                    <td>{lostItem.subType}</td>
                                    <td>{lostItem.region}</td>
                                    <td>{lostItem.district}</td>
                                    <td>{lostItem.organization}</td>
                                    <td>{lostItem.description}</td>
                                    <td>{lostItem.lostDate}</td>
                                    <td>{lostItem.registry}</td>
                                    <td>
                                        {
                                            lostItem.ownerid === currentUser.id ?
                                                <button onClick={() => reviewLostItem(lostItem)} className='btn btn-warning'>Checked</button>
                                                :
                                                <button onClick={() => reviewLostItem(lostItem)} className='btn btn-success'>Rasm solish</button>

                                        }
                                    </td>
                                </tr>)
                            }
                        </tbody>
                    </table>
                </div>
            </div>
            <Rodal height={500} width={800} visible={visible} onClose={toggle}>
                <div className='review-header'>
                    <h5>E'lon raqami: {aLostItem.registry}</h5>
                </div>
                <div className="review-body">
                    <p>E'lon beruvchi: {aLostItem.username}</p>
                    <p>E'lon holati: {aLostItem.username}</p>
                    <p>E'lonning qisqacha tavsifi: {aLostItem.description}</p>
                    <p>Turi: {aLostItem.type}</p>
                    <p>Sub turi: {aLostItem.subType}</p>
                    <p>Hudud: {aLostItem.region}</p>
                    <p>Tuman: {aLostItem.district}</p>
                    <p>Status: {aLostItem.status}</p>
                    <p>Buyum yoki Hujjat holati: {!aLostItem.found && aLostItem.status === "YOQOTIB_QOYILGAN" ? "Topilmagan" : !aLostItem.found && aLostItem.status === "TOPIB_OLINGAN" ? "Qaytarib berilmagan" : ""}</p>
                    <p>Mas'ul tashkilot: {aLostItem.organization}</p>
                </div>
                <div className="review-footer">
                    {
                        aLostItem.ownerid === currentUser.id ?
                            <button className='btn btn-warning'>Checked</button>
                            :
                            <button onClick={() => changeOwner(aLostItem.id)} className='btn btn-info'>{!aLostItem.found && aLostItem.status === "YOQOTIB_QOYILGAN" ? "Topgan bo'lishim mumkin" : !aLostItem.found && aLostItem.status === "TOPIB_OLINGAN" ? "Yo'qotgan bo'lishim mumkin" : ""}</button>
                    }
                </div>
            </Rodal>
        </div>
    )
}

export default Main;
