import React, { useEffect, useState } from "react";
import { apiCall } from './utils/apiCall';
import { useLocation } from "react-router-dom";

function Posts() {
    const [posts, setPosts] = useState({});
    const location = useLocation();
    useEffect(() => {
        getPosts(1);
        console.log(posts);
    }, [])
    function getPosts(page) {
        apiCall('/post/' + location.pathname.slice(6) + "?limit=5&page="+page, 'get').then(res => {
            setPosts(res.data)
            console.log(res.data.data);
        })
    }

    function generateBtn() {
        let a = posts.pageCount;
        let b = [];
        for (let i = 1; i <= a; i++) {
            b.push(i);
        }
        return (
            <div>
                {
                    b.map(btn => <button className="btn btn-info m-2" onClick={()=>getPosts(btn)}>{btn}</button>)
                }
            </div>
        )
    }

    return (
        <div className="container">
            <div className="row">
                <div className="col-12">
                    <h1 className="text-center">Posts</h1>
                </div>
                <table className="table">
                    <thead>
                        <tr>
                            <th>t/r</th>
                            <th>title</th>
                            <th>body</th>
                        </tr>
                    </thead>
                    <tbody>
                        {
                            posts.data ? posts.data.map(post => <tr>
                                <td>{post.id}</td>
                                <td>{post.title}</td>
                                <td>{post.body}</td>
                            </tr>) : ""
                        }
                    </tbody>
                </table>
            </div>
        </div>
    )
}

export default Posts;
