import React, { useEffect, useState } from 'react';
import {apiCall} from './utils/apiCall'


function App(){

    const [todos, setTodos] = useState([]);


    useEffect(()=>{
        getTodos();
    }, [])

    function getTodos(){
        apiCall('/todo', 'get').then(res=>{
            setTodos(res.data)
            console.log(res.data);
        })
    }


    return (
        <div>
            <table className="table">
                <thead>
                    <th>id</th>
                    <th>name</th>
                    <th>description</th>
                    <th>status</th>
                </thead>
                <tbody>
                    {
                        todos.map(todo=> <tr>
                            <td>{todo.id}</td>
                            <td>{todo.name}</td>
                            <td>{todo.description}</td>
                            <td>
                                {
                                    todo.status ? <button>Completed</button> : <button>Pending</button>
                                }
                            </td>
                        </tr>)
                    }
                </tbody>
            </table>
        </div>
    )
}

export default App;
