import { call, takeLatest, put } from "redux-saga/effects";
import { getPostSuccess, getPosts } from "../slice/PostReducer";
import { apiCall } from "../slice/apiCall";


export function* workGetPosts(action) {
    try {
        console.log(action)
        const res = yield call(apiCall, "/post/"+action.payload, "get");
        yield put(getPostSuccess(res.data))
    } catch (err) {

    }
}


export function* postSaga() {
    yield takeLatest(getPosts().type, workGetPosts)
}
