import React from 'react';
import {apiCall} from '../utils/apiCall'
import { useNavigate } from 'react-router-dom';

function Register() {

    const navigate = useNavigate();

    function submit(e){
        e.preventDefault();
        let data = {fullName: e.target[0].value, username: e.target[1].value, email: e.target[2].value, password: e.target[3].value};
        apiCall('/auth/register', 'post', data).then(res=>{
            if(res.data.id){
                console.log(res.data);
                navigate('/login')
            }else{
                console.log("yo");
                e.target[1].value = res.data
                e.target[1].style.color = 'red'
            }
        })
    }

    return (
        <div className='container'>
            <div className="row d-flex justify-content-center">
                <div className="card col-5">
                    <form onSubmit={submit} action="">
                        <div className="card-header">
                            <h5 className='text-center'>Register</h5>
                        </div>
                        <div className="card-body">
                            <input type="text" className='form-control my-2' placeholder='Firstname...' />
                            <input type="text" className='form-control my-2' placeholder='Username...' />
                            <input type="text" className='form-control my-2' placeholder='Email...' />
                            <input type="text" className='form-control my-2' placeholder='Password...' />
                        </div>
                        <div className="card-footer">
                            <button className='btn btn-warning w-100'>submit</button>
                            <a href="http://localhost:3000/login">have you an account?</a>
                        </div>
                    </form>
                </div>
            </div>
        </div>
    );
}

export default Register;
