import {call, takeLatest, put, take} from "redux-saga/effects";
import {apiCall} from "../slice/apiCall.js";
import { getCategories, getCategoriesSuccess, getProducts, getProductsSuccess } from "../slice/userReducer.js";

export function* workGetCategories(action){
    const res = yield call(apiCall, "/category", "get");
    yield put(getCategoriesSuccess(res.data));
}

export function* workGetProducts(action){
    const res = yield call(apiCall, "/"+action.payload+"/product", "get");
    yield put(getProductsSuccess(res.data));
}

export function* userSaga() {
    yield takeLatest(getCategories().type, workGetCategories);
    yield takeLatest(getProducts().type, workGetProducts)
}
