import React from "react";
import { apiCall } from './utils/apiCall';


function Login() {

    function register(e) {
        e.preventDefault();
        let data = { username: e.target[0].value, password: e.target[1].value}
        apiCall('/auth/register', 'post', data).then(res => {
            console.log(res.data);
            e.target[0].value = '';
            e.target[1].value = '';
        })
    }

    return (
        <div className="row">
            <div className="container d-flex justify-content-center">
                <div className="card col-5 mt-5">
                    <form onSubmit={register} action="">
                        <div className="card-header">
                            <h5 className="text-center">Login</h5>
                        </div>
                        <div className="card-body">
                            <input type="text" className="form-control my-2" placeholder="UserName..." />
                            <input type="text" className="form-control my-2" placeholder="Password..." />
                        </div>
                        <div className="card-footer">
                            <button className="btn btn-warning w-100">submit</button>
                        </div>
                    </form>
                </div>
            </div>
        </div>
    )
}

export default Login;
