import {createSlice} from "@reduxjs/toolkit";

const state = {
    users: [],
    totalPages: 0
}

const slice = createSlice({
    name: "user",
    initialState: state,
    reducers: {
        getUsers(state, action){},
        getUsersSuccess(state,action){
            state.users = action.payload.userList;
            state.totalPages = action.payload.totalPages;
        },
        getTwoRich(state, action){},
        getTwoRichSuccess(state,action){
            state.users = action.payload.userList;
            state.totalPages = action.payload.totalPages;
        },
        getOldUsers(state,action){},
        getOldUsersSuccess(state,action){
            state.users = action.payload.userList;
            state.totalPages = action.payload.totalPages;
        },
        searchUsers(state, action){},
        getSearchSuccess(state,action){
            state.users = action.payload.userList;
            state.totalPages = action.payload.totalPages;
        }
    }
})

export const { getUsers, getUsersSuccess, getTwoRich, getOldUsers, searchUsers, getOldUsersSuccess, getTwoRichSuccess, getSearchSuccess } = slice.actions
export default slice.reducer
