import React, { useEffect, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { changeOrderproduct, deleteOrder } from '../store/slice/userReducer';

function OrderProduct() {

    const {orderProducts, totalBalance} = useSelector(state => state.userReducer);
    const dispatch = useDispatch();

    function increment(index){
        dispatch(changeOrderproduct({status: "plus", index}))
    }

    function decrement(index){
       dispatch(changeOrderproduct({status: "minus", index}))
    }

    function orderDelete(index){
        dispatch(deleteOrder(index))
    }



    return (
        <div>
            <table className='table'>
                <thead>
                    <tr>
                        <th>t/r</th>
                        <th>name</th>
                        <th>amount</th>
                        <th>price</th>
                        <th>actions</th>
                    </tr>
                </thead>
                <tbody>
                    {
                        orderProducts.map((order, index)=> <tr key={order.id}>
                            <td>{order.id}</td>
                            <td>{order.name}</td>
                            <td>{order.amount * order.count}</td>
                            <td>
                                <button onClick={()=> increment (index)} className='btn btn-warning mx-1'>+</button>
                                {order.count} 
                                <button onClick={()=> decrement(index)} className='btn btn-warning mx-1'>-</button> 
                                </td>
                            <td>
                                <button onClick={()=> orderDelete(index, order)} className='btn btn-danger'>delete</button>
                            </td>
                        </tr>)
                    }
                </tbody>
            </table>

            <div>
                <h5>total balance: {totalBalance}</h5>
                <button className='btn btn-warning my-2 mx-2'>check over</button>
            </div>
        </div>
    );
}

export default OrderProduct;
