import React, { useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { addExpanseToUser, addInCome, getUsers } from './store/slice/userReducer';
import './index.scss'

function App() {

    const { users } = useSelector(state => state.userReducer);
    const dispatch = useDispatch();

    useEffect(() => {
        dispatch(getUsers());
        console.log(users);
    }, [])


    function addInComed() {
        let select = document.getElementById("income");
        let value = document.getElementById('amount');
        console.log(select.value, value.value);
        dispatch(addInCome({ receivingUserId: Number(select.value), moneyAmount: value.value }));
    }

    function addExpanse() {
        let receivingId = document.getElementById("receivingId")
        let givingId = document.getElementById("givingId")
        let moneyAmount = document.getElementById("moneyAmount")
        dispatch(addExpanseToUser({ receivingUserId: Number(receivingId.value), givingUserId: Number(givingId.value), moneyAmount: Number(moneyAmount.value) }))
        console.log(receivingId.value, givingId.value, moneyAmount.value);
    }

    return (
        <div>
            <div className='card col-4'>
                <div className="card-header">
                    <select className='select ' id='income'>
                        {
                            users.map(user => <option value={user.id}>{user.fullName}</option>)
                        }
                    </select>
                </div>
                <input id='amount' type="text" />
                <button onClick={addInComed}>send</button>
            </div>
            <div>
                <div>
                    from: <select name="" id="givingId">
                        {
                            users.map(user => <option value={user.id}>{user.fullName}</option>)
                        }
                    </select>
                    to: <select name="" id="receivingId">
                        {
                            users.map(user => <option value={user.id}>{user.fullName}</option>)
                        }
                    </select>
                    amount: <input type="text" id='moneyAmount' />
                    <button onClick={addExpanse}>save</button>
                </div>
                <table className='table'>
                    <thead>
                        <tr>
                            <th>t/r</th>
                            <th>fullName</th>
                            <th>phone number</th>
                            <th>money</th>
                        </tr>
                    </thead>
                    <tbody>
                        {
                            users.map(user => <tr>
                                <td>{user.id}</td>
                                <td>{user.fullName}</td>
                                <td>{user.phoneNumber}</td>
                                <td>{user.moneyAmount}</td>
                            </tr>)
                        }
                    </tbody>
                </table>
            </div>
        </div>
    );
}

export default App;
