import { useEffect, useState } from 'react';
import {useDispatch, useSelector} from 'react-redux'
import Carousel from 'react-bootstrap/Carousel';
import { getLostItems } from '../store/slice/lostItemReducer';

function Carusel() {
  const [index, setIndex] = useState(0);
  const {lostItems} = useSelector(state=>state.lostItemReducer);
  const [carusel, setCarusel] = useState([]);
  const dispatch = useDispatch();
  const [filter, setFilter] = useState({
    holat: '',
    type: '',
    subType: '',
    region: '',
    district: ''
});

  const handleSelect = (selectedIndex) => {
    setIndex(selectedIndex);
  };

  useEffect(()=>{
    dispatch(getLostItems(filter))
    setCarusel(lostItems.map((item, index)=> index < 5));
    console.log(lostItems);
  }, [])

  return (
    <Carousel activeIndex={index} onSelect={handleSelect}>
      <Carousel.Item>
        <div>carusel 1</div>       
      </Carousel.Item>
      <Carousel.Item>
        <div >carusel 2</div>
      </Carousel.Item>
      <Carousel.Item>
        <div>carusel 3</div>    
      </Carousel.Item>
    </Carousel>
  );
}

export default Carusel;
