import {call, takeLatest, put} from "redux-saga/effects";
import {apiCall} from "../slice/apiCall.js";
import { getUserChats, getUserChatsSuccess } from "../slice/chatReducer.js";

export function* workGetUserChats(action){
    const res = yield call(apiCall, "/chat/"+action.payload, "get");
    console.log(res.data, action.payload);
    yield put(getUserChatsSuccess(res.data))
}

export function* chatSaga() {
    yield takeLatest(getUserChats().type, workGetUserChats)
}
