import {createSlice} from "@reduxjs/toolkit";

const state = {
    users: [],
}

const slice = createSlice({
    name: "user",
    initialState: state,
    reducers: {
        getUser(){
            console.log("salom");
        },
        getUserSuccess(state, action){
            console.log("qanday");
            console.log(action);
            state.users = action.payload;
        }
    }
})

export const { getUser, getUserSuccess } = slice.actions
export default slice.reducer
