import React from 'react';
import './auth.scss';
import {useForm} from 'react-hook-form'
import { apiCall } from '../../utils/apiCall';
function Auth() {

    const {reset, register, handleSubmit} = useForm();

    function submit(data){
        apiCall('/auth', "post", {userName: data.username, password: data.password}).then(res=>{
            localStorage.setItem('token', JSON.stringify(res.data.id))
        })
        reset();
    }
    return (
        <div className='auth'>
            <div className="card col-5">
                <div className="card-header">
                    <h3 className='text-center'>Login</h3>
                </div>
                <div className="card-body">
                    <form onSubmit={handleSubmit((data)=>submit(data))} id="form">
                        Username
                        <input {...register('username')} className='form-control my-2' type="text" />
                        Password
                        <input {...register('password')} className='form-control my-2' type="password" />
                    </form>
                </div>
                <div className="card-footer">
                    <button type='submit' form='form' className='my-3 btn btn-success w-100'>Send</button>
                </div>
            </div>
        </div>
    );
}

export default Auth;
