import React, { useEffect } from 'react';
import { Client } from '@stomp/stompjs';

const App = () => {
    // SockJS ulanishini yaratish
let socket = new SockJS('http://localhost:8080/app');

// STOMP ulanishini yaratish
let stompClient = Stomp.over(socket);

// STOMP ulanishni boshlash
stompClient.connect({}, function (frame) {
  console.log('Ulanish muvaffaqiyatli amalga oshirildi.');

  // Xabar almashish uchun obunani yaratish
  let subscription = stompClient.subscribe('/topic/messages', function (message) {
    console.log('Yangi xabar keldi: ' + message.body);
  });

  // Xabar yuborish
});

// STOMP ulanishni to'xtatish
function disconnect() {
  if (stompClient !== null) {
    stompClient.disconnect();
    console.log('Ulanish to\'xtatildi.');
  }
}


function send(){
  stompClient.send('/app/sendMessage', {}, 'Salom, dunyo!');

}

    return <div>
        MyComponent
        <button onClick={send}> ok</button>
    </div>;
};

export default App;
