import {all} from "redux-saga/effects"
import {userSaga} from "./userSaga";
import {chatSaga} from "./chatSaga";
import {messageSaga} from "./messageSaga";


export default function* rootSaga() {
    yield all(
        [
            userSaga(),
            chatSaga(),
            messageSaga()
        ]
    )
}
