import { call, takeLatest, put } from "redux-saga/effects";
import { apiCall } from "../slice/apiCall";
import { getLoans, getLoansSuccess } from "../slice/loanReducer";

export function* workGetLoan(action){
    const res = yield call(apiCall, "/loan/"+action.payload, "get");
    yield put(getLoansSuccess(res.data))

}

export function* loanSaga(){
    yield takeLatest(getLoans().type, workGetLoan);
}
