import React, { useEffect, useState } from "react";
import { apiCall } from './utils/apiCall';
import './index.css';
import Rodal from "rodal";
import 'rodal/lib/rodal.css'
function Home() {

    const [currentUser, setCurrentUser] = useState({});
    const [posts, setPosts] = useState([]);
    const [comments, setComments] = useState({ id: 0, content: "", comments: [], index: undefined });
    const [visible, setVisible] = useState(false);

    useEffect(() => {
        getCurrentUser();
        getPosts();
    }, [])

    function getCurrentUser() {
        apiCall('/user', 'get', {}, "userId").then(res => {
            setCurrentUser(res.data);
        })
    }

    function toggle() {
        setVisible(prev => !prev);
    }

    function getPosts() {
        apiCall('/post', 'get').then(res => {
            setPosts(res.data);
        })
    }

    function addLike(postId) {
        apiCall('/like', 'post', { postId, userId: Number(localStorage.getItem("userId")) }).then(res => {
            getPosts();
        })
    }

    function addCommentLike(commentId) {
        apiCall('/like/comment', 'post', { commentId, userId: Number(localStorage.getItem("userId")) }).then(res => {
            getPosts();
        })
    }

    function getComments(comment, content, id, index) {

        setComments({ id, content, comments: comment, index });
        toggle();
    }

    function addComment(e) {
        if (e.target.value.length > 2 && e.charCode === 13) {
            apiCall('/comment', 'post', { content: e.target.value, postId: comments.id, userId: Number(localStorage.getItem("userId")) }).then(res => {
                getPosts();
                setComments({ ...comments, comments: posts[comments.index].comments })
                e.target.value = "";
            })
        }
    }

    function addPost() {
        let postContent = document.getElementById('post')
        apiCall('/post', 'post', { content: postContent.value, userId: Number(localStorage.getItem("userId")) }).then(res => {
            getPosts();
            postContent.value = "";
        })
    }

    return (
        <div>
            
        </div>
    )
}

export default Home;
