import {call, takeLatest, put} from "redux-saga/effects";
import {apiCall} from "../slice/apiCall.js";
import { getMessages, getMessagesSuccess } from "../slice/messageReducer.js";

export function* workGetMessages(action){
    const res = yield call(apiCall, "/message/"+action.payload);
    yield put(getMessagesSuccess(res.data))
}

export function* messageSaga() {
    yield takeLatest(getMessages().type, workGetMessages)
}
