import { useDispatch } from 'react-redux';
import { apiCall } from '../utils/apiCall';
import React, { useState, useEffect } from 'react';
import { saveLostItems } from '../store/slice/lostItemReducer';
import { useNavigate } from 'react-router-dom';

function SaveLostItems() {
    const [types, setTypes] = useState([]);
    const [subTypes, setSubTypes] = useState([]);
    const [regions, setRegions] = useState([]);
    const [districts, setDistricts] = useState([]);
    const dispatch = useDispatch();
    const navigate = useNavigate();

    useEffect(() => {
        getTypes();
        getRegions();
    }, [])

    function getTypes() {
        apiCall('/api/type', 'get', null, {}).then(res => {
            setTypes(res.data)
        })
    }
    function getSubTypes(typeId) {
        if (typeId) {
            apiCall('/api/type/subtype/' + typeId, 'get', null, {}).then(res => {
                setSubTypes(res.data)
            })
        }
    }

    function getRegions() {
        apiCall('/api/type/region', 'get', null, {}).then(res => {
            setRegions(res.data)
        })
    }

    function getDistrict(regionId) {
        if (regionId) {
            apiCall('/api/type/district/' + regionId, 'get', null, {}).then(res => {
                setDistricts(res.data)
            })
        }
    }

    function changeType(e) {
        getSubTypes(e.target.value);
    }

    function changeRegion(e) {
        getDistrict(e.target.value)
    }

    function submit(e){
        e.preventDefault();
        let data = {typeId: e.target[1].value, subTypeId: e.target[2].value, regionId: e.target[3].value, districtId: e.target[4].value, organization: e.target[5].value, status: e.target[0].value, description: e.target[6].value}
        apiCall("/api/lost", 'post', data).then(res=>{
            console.log(res.data);
        }).catch(err=>{
            navigate('/login')
        })
    }   

    return (
        <div>
            <div className='row'>
                <div className="col-6">
                    <div className="card">
                        <form onSubmit={submit} action="">
                            <div className="card-header">
                                <h4 className='text-center'>Ariza yuborish</h4>
                            </div>
                            <div className="card-body">
                                <div>
                                    <select className="form-control py-1 my-2" name="" id="">
                                        <option value="">Holatni tanlang</option>
                                        <option value="YOQOTIB_QOYILGAN">YOQOTIB_QOYILGAN</option>
                                        <option value="TOPIB_OLINGAN">TOPIB_OLINGAN</option>
                                    </select>
                                </div>
                                <div>
                                    <select className="form-control py-1 my-2" onChange={(e) => changeType(e)} name="" id="">
                                        <option value="">Ashyo turini tanlang</option>
                                        {
                                            types.map(type => <option value={type.id}>{type.name}</option>)
                                        }
                                    </select>
                                </div>
                                <div>
                                    <select className="form-control py-1 my-2" name="" id="">
                                        <option value="">Ashyo sub turini tanlang</option>
                                        {
                                            subTypes?.map(subtype => <option value={subtype.id}>{subtype.name}</option>)
                                        }
                                    </select>
                                </div>
                                <div>
                                    <select className="form-control py-1 my-2" onChange={(e) => changeRegion(e)} name="" id="">
                                        <option value="">Hududni tanlang</option>
                                        {
                                            regions.map(region => <option value={region.id}>{region.name}</option>)
                                        }
                                    </select>
                                </div>
                                <div>
                                    <select className="form-control py-1 my-2" name="" id="">
                                        <option value="">Tumanni tanlang</option>
                                        {
                                            districts?.map(district => <option value={district.id}>{district.name}</option>)
                                        }
                                    </select>
                                </div>
                                <div>
                                    <input className="form-control py-1 my-2" type="text" placeholder="Mas'ul tashkilot nomini kiriting" />
                                </div>
                                <div>
                                    <input className="form-control py-1 my-2" type="text" placeholder="Qisqacha ma'lumot" />
                                </div>
                            </div>
                            <div className="card-footer">
                                <button className='btn btn-warning'>submit</button>
                            </div>
                        </form>
                    </div>
                </div>
            </div>
        </div>
    );
}

export default SaveLostItems;
