import React, { useEffect, useState } from "react";
import { apiCall } from './utils/apiCall';
import './index.css';
import Rodal from "rodal";
import 'rodal/lib/rodal.css'
function Home() {

    const [posts, setPosts] = useState([]);
    const [users, setUsers] = useState([]);
    const [select, setSelect] = useState('All')

    useEffect(() => {
        getPosts(0, 1);
        getUsers();
    }, [])

    function getPosts(id, page) {
        let url = id <= 0 || id === 'All' ? '' : '/' + id;
        apiCall('/post' + url + '?pageNumber=' + page + '&limit=5', 'get').then(res => {
            setPosts(res.data)
        })
    }

    function changeSelect(e) {
        setSelect(e.target.value)
        if (e.target.value !== 'All') {
            getPosts(e.target.value, 1)
        } else {
            getPosts(0, 1)
        }
    }

    function getUsers() {
        apiCall('/user', 'get').then(res => {
            setUsers(res.data);
        })
    }

    function paginate(number) {
        getPosts(select, number);
    }

    function generateBtn() {
        let a = posts[0]?.pageCount;
        let b = [];
        for (let i = 1; i <= a; i++) {
            b.push(i);
        }
        return (
            <div>
                {
                    b.map(btn => <button className="btn btn-info m-2" onClick={() => paginate(btn)}>{btn}</button>)
                }
            </div>
        )
    }


    return (
        <div className="row">
            <div className="col-4">
                <select className="form-control my-2" onChange={(e) => changeSelect(e)} name="" id="select">
                    <option value="All">All</option>
                    {
                        users.map(user => <option value={user.id}>{user.fullName}</option>)
                    }
                </select>

                <div className="card">
                    <div className="card-header">
                        <h6 className="text-center">Add post</h6>
                    </div>
                </div>
            </div>
            <div className="col-8 ">
                <h3>Posts</h3>
                <div className="content">
                {
                    posts.map(post=> <div className="card">
                        <div className="card-header">
                            <h6>{post.title}</h6>
                        </div>
                        <div className="card-body">
                            <p>{post.description}</p>
                        </div>
                    </div>)
                }
                </div>
                {generateBtn()}
            </div>

            
        </div>
    )
}

export default Home;
