import React, { useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { addInCome, getUsers } from './store/slice/userReducer';

function App() {

    const {users} = useSelector(state=>state.userReducer);
    const dispatch = useDispatch();

    useEffect(()=>{
        dispatch(getUsers());
        console.log(users);
    }, [])


    function addInComed(){
        let select = document.getElementById("income");
        let value = document.getElementById('amount');
        console.log(select.value, value.value);
        dispatch(addInCome({receivingUserId: Number(select.value), moneyAmount: value.value}));
    }

    return (
        <div>
            <div>
                <select id='income'>
                    {
                        users.map(user=> <option value={user.id}>{user.fullName}</option>)
                    }
                </select>
                <input id='amount' type="text" />
                <button onClick={addInComed}>send</button>
            </div>
            <div>
                <div>
                    from: <select name="" id="">
                    {
                        users.map(user=> <option>{user.fullName}</option>)
                    }
                    </select>
                    to: <select name="" id="">
                    {
                        users.map(user=> <option>{user.fullName}</option>)
                    }
                    </select>
                    amount: <input type="text" />
                    <button>save</button>
                </div>
                <table className='table'>
                    <thead>
                        <tr>
                            <th>t/r</th>
                            <th>fullName</th>
                            <th>phone number</th>
                            <th>money</th>
                        </tr>
                    </thead>
                    <tbody>
                        {
                            users.map(user=><tr>
                                <td>{user.id}</td>
                                <td>{user.fullName}</td>
                                <td>{user.phoneNumber}</td>
                                <td>{user.moneyAmount}</td>
                            </tr>)
                        }
                    </tbody>
                </table>
            </div>
        </div>
    );
}

export default App;
