import React, { useEffect, useState } from "react";
import { apiCall } from '../utils/apiCall';
import Rodal from "rodal";
import 'rodal/lib/rodal.css'
import { useSubscription } from "react-stomp-hooks";
import { Link, Outlet, useNavigate } from "react-router-dom";

function NewMessage() {

    const [users, setUsers] = useState([]);
    const [currentUser, setCurrentUser] = useState([]);
    const [searchUser, setSearchUser] = useState([]);

    useEffect(() => {
        getChats();
        getCurrentUser();
    }, [])

    function getCurrentUser(){
        apiCall('/user/'+localStorage.getItem("userId"), 'get').then(res=>{
            setCurrentUser(res.data.data)
        })
    }

    function saveMessage(e) {
        e.preventDefault();
        let data = { senderEmail: currentUser.email, title: e.target[1].value, text: e.target[2].value, chatId:  e.target[0].value, sent: true};
        apiCall('/message', 'post', data).then(res=>{
            console.log(res.data);
        })
    }

    function draftMessage() {
        let form = document.getElementById('form');
        let data = { senderEmail: currentUser.email, title: form[1].value, text: form[2].value, chatId:  form[0].value, sent: false};
        apiCall('/message', 'post', data).then(res=>{
            console.log(res.data);
        })
    }

    function getChats() {
        apiCall('/chat/' + localStorage.getItem("userId"), 'get').then(res => {
            setUsers(res.data);
        })
    }

    function changeInp(e) {
        if(e.keyCode === 13){
            apiCall('/user/search?email='+e.target.value, 'get').then(res=>{
                if(res.data.message === 'success'){
                    setSearchUser(res.data.data)
                }
            })
        }
    }

    function addChat() {
        
    }

    return (
        <div className="row">
            <div className="col-12">
                <input type="text" onKeyUp={changeInp} id="search" className="form-control my-2 w-50" />
                {
                    searchUser.id ? <div>
                        <p>{searchUser.firstName}</p>
                        <p>{searchUser.lastName}</p>
                        <button onClick={addChat} className="btn btn-info m-2">add to contact</button>
                    </div> : ""
                }
            </div>
            <div className="card col-6">
                <form id="form" action="" onSubmit={saveMessage}>
                    <div className="card-header row">
                        <select name="" className="form-control my-2" id="select">
                            {
                                users.map(user => <option value={user.chatId}>{user.email}</option>)
                            }
                        </select>
                    </div>
                    <div className="card-body">
                        <input type="text" className="form-control" />
                        <input type="text" className="form-control" />
                    </div>
                    <div className="card-footer">
                        <button onClick={draftMessage} className="btn btn-light w-100">draft</button>
                        <button  className="btn btn-warning w-100">submit</button>

                    </div>
                </form>
            </div>
        </div>
    )
}

export default NewMessage;
