import React, { useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { getUsers } from './store/slice/userReducer';

function App() {

    const {users} = useSelector(state => state.userReducer);
    const dispatch = useDispatch();


    useEffect(()=>{
        dispatch(getUsers({limit: 5, pageIndex: 1}));
    }, [])


    return (
        <div className='row'>
            <div className='col-3'>
                <button className="btn btn-warning w-100 my-2 mx-3">All</button>
                <button className='btn btn-warning w-100 my-2 mx-3'>Two Rich</button>
                <button className='btn btn-warning w-100 my-2 mx-3'>Old Users</button>
            </div>
            <div className="col-8 m-3">
                <div>
                    <h1>Header</h1>
                </div>
                <div>
                    <table className="table">
                        <thead>
                            <tr>
                                <th>t/r</th>
                                <th>full Name</th>
                                <th>phone Number</th>
                                <th>age</th>
                                <th>money</th>
                                <th>address</th>
                            </tr>
                        </thead>
                        <tbody>
                            {
                                users.map((user, index)=> <tr>
                                    <td>{index+1}</td>
                                    <td>{user.fullName}</td>
                                    <td>{user.phoneNumber}</td>
                                    <td>{user.age}</td>
                                    <td>{user.moneyAmount}</td>
                                    <td>{user.address}</td>
                                </tr>)
                            }
                        </tbody>
                    </table>
                </div>
            </div>
        </div>
    );
}








export default App;
