import React, { useEffect, useState } from 'react';
import './home.scss'
import Rodal from 'rodal';
import 'rodal/lib/rodal.css'
import { set, useForm } from 'react-hook-form';
import { apiCall } from '../../utils/apiCall';


function Home() {

    const [visible, setVisible] = useState(false);
    const { register, handleSubmit, formState: { errors } } = useForm();
    const [tasks, setTasks] = useState([]);
    const [visible2, setVisible2] = useState(false);
    const [users, setUsers] = useState([]);
    const [check, setCheck] = useState(false);
    const [taskUser, setTaskUser] = useState({ taskId: "", usersId: [] })
    const [task, setTask] = useState({});

    useEffect(() => {
        getAllTasks();
    }, [])

    function getAllTasks() {
        apiCall("/user-task/all", "get").then(res => {
            setTasks([...res.data])
        })
    }

    function filterTasks(drawStatus) {
        let arr = [];
        for (let i = 0; i < tasks.length; i++) {
            if (tasks[i].status === drawStatus) {
                arr.push(tasks[i])
            }
        }
        return arr;
    }

    function toggle() {
        setVisible(prev => !prev)
    }

    function submit(data) {
        apiCall('/task', 'post', data).then(res => {
            console.log("task post boldi");
            toggle()
            getAllTasks();
        })
    }



    function allowDrag(e) {
        e.preventDefault();
    }

    function drop(e, status) {
        let data = e.dataTransfer.getData("text");
        // e.target.appendChild(document.getElementById(data));
        apiCall("/task", "put", { id: data, status }).then(res => {
            filterTasks(status)
            draw(status)
            getAllTasks();
            console.log('ozgardi');
        })
    }

    function drag(e) {
        e.dataTransfer.setData("text", e.target.id);
        console.log(e.target);


    }

    function postUserTask() {
        for (let i = 0; i <= users.length; i++) {
            if (users[i].taskId !== null) {
                apiCall("/user-task/users", 'post', taskUser).then(res => {
                    getAllTasks();
                })
            }
        }
        toggle2("")

    }


    function toggle2(task) {
        if (task) {
            setTask(task);
            taskUser.taskId = task.taskId;
            setTaskUser({ ...taskUser });

            apiCall("/user-task/assign/" + task.taskId, "get").then(res => {
                setUsers([...res.data])
                console.log(res.data);
            })

        }
        setVisible2(prev => !prev);
        console.log(users);
    }

    function getUser(e, index) {
        console.log(users, index);
        if (e.target.checked) {
            console.log(users[index]);
            users[index].taskId = task.taskId;
        }
        if (!e.target.checked) {
            users[index].taskId = null;
        }
        setUsers([...users])
    }

    function show(e) {
        e.target.children[0].style.display = 'block'
    }


    function hide(e) {
        e.target.children[0].style.display = 'none'
    }


    function draw(status) {
        return filterTasks(status).map((task, index) => <div key={task.taskId} id={task.taskId} draggable={true} onDragStart={(event => drag(event))} className="task card my-2 mx-2">
            <div className="card-header">
                <h5 className='text-center'>{task.title}</h5>
            </div>
            <div className="card-body">
                <p>{task.description}</p>
                <p>{task.status}</p>
            </div>
            <div className="card-footer d-flex">
                <div className='wrap-assign'>
                    {
                        task.userRes.map((user, index) => <p onMouseEnter={(e) => show(e)} onMouseLeave={(e) => hide(e)} className='assignment'>{user.firstName[0] + user.lastName[0]} <p>{user.firstName + " " + user.lastName}</p></p>)
                    }
                </div>
                <div>
                    <button onClick={() => toggle2(task)} className='btn btn-success'>
                        +
                    </button>
                </div>
            </div>
        </div>)
    }

    return (
        <div className='wrapper'>
            <div className='header'>Header
                <button onClick={toggle} className='btn btn-success'>Add task +</button>
            </div>
            <div className="container">
                <div className="content">
                    <div className="wrap-task">
                        <h4 className='text-center'>TO DO</h4>
                        <div onDrop={event => drop(event, "TO_DO")} onDragOver={(event) => allowDrag(event)} className='tasks'>
                            {draw("TO_DO")}
                        </div>
                    </div>
                    <div className="wrap-task">
                        <h4 className='text-center'>IN PROGRESS</h4>
                        <div onDrop={event => drop(event, "IN_PROGRESS")} onDragOver={(event) => allowDrag(event)} className="tasks">
                            {draw("IN_PROGRESS")}
                        </div>
                    </div>
                    <div className="wrap-task">
                        <h4 className="text-center">COMPLETE</h4>
                        <div onDrop={event => drop(event, "COMPLETE")} onDragOver={(event) => allowDrag(event)} className="tasks">
                            {draw("COMPLETE")}
                        </div>
                    </div>
                    <div className="wrap-task">
                        <h4 className="text-center">DONE</h4>
                        <div onDrop={event => drop(event, "DONE")} onDragOver={(event) => allowDrag(event)} className="tasks">
                            {draw("DONE")}
                        </div>
                    </div>
                </div>
            </div>


            <Rodal width={600} height={400} onClose={toggle2} visible={visible2}>
                <div className='wrap-checks'>
                    <div className='checkboxes'>
                        {
                            users.map((user, index) => <div className='checks'>
                                {/* {console.log(findTaskAssign(user.id))} */}
                                <input checked={user.taskId == task.taskId} onChange={(event) => getUser(event, user, index)} type="checkbox" />
                                <p>{user.firstName + " " + user.lastName}</p>
                            </div>)
                        }
                    </div>
                    <button onClick={() => postUserTask()} className="btn btn-success my-2 w-100">Send</button>
                </div>
            </Rodal>





            <Rodal visible={visible} onClose={toggle}>
                <form onSubmit={handleSubmit((data) => submit(data))}>
                    Title
                    <div className="wrap-inp">
                        <input {...register('title', { required: "This is required" })} type="text" className="form-control my-2" />
                        <p className="error">{errors.title?.message}</p>
                    </div>
                    Description
                    <div className="wrap-inp">
                        <input {...register('description', { required: "This is required" })} type="text" className="form-control my-2" />
                        <p className="error">{errors.description?.message}</p>
                    </div>
                    <button type='submit' className="btn btn-success my-2 w-100">Send</button>
                </form>
            </Rodal>
        </div>
    );
}

export default Home;
