import { call, takeLatest, put } from "redux-saga/effects";
import { apiCall } from "../slice/apiCall";
import { getPayments, getPaymentsSuccess } from "../slice/paymentReducer";

export function* workGetPayments(action){
    const res = yield call(apiCall, "/payment/"+action.payload, "get");
    yield put(getPaymentsSuccess(res.data));
}

export function* paymentSaga(){
    yield takeLatest(getPayments().type, workGetPayments)
}
