import React,{useContext, useEffect, useState} from 'react';
import { Context } from '../../storage/Context';
import { apiCall } from '../../utils/apiCall';
function Card() {
    const {state, dispatch} = useContext(Context);
    const [orderProducts, setOrderProducts] = useState([])
    useEffect(()=>{
        apiCall("/order/"+localStorage.getItem("token"), "get").then(res=>{
            console.log(res.data);
            apiCall("/order-product/"+res.data.data.id, "get").then(respon=>{
                for (let i = 0; i < res.data.data.length; i++) {
                    console.log('salom');
                    apiCall("/product/"+respon.data.data[i].productId, "get").then(response=>{
                        orderProducts.push(response.data.data)
                        setOrderProducts([...orderProducts])
                        console.log(orderProducts);
                    })
                }
            })
        })
    },[])
    return (
        <div>
            <div className="header">

            </div>
            <div className="content">
                <div className="row">
                    {
                        orderProducts.map((product, index)=><div key={product.id} className='card col-2 my-1 mx-1'>
                        <div className="card-header">
                            <h4 className='text-center'>{product.name}</h4>
                        </div>
                        <div className="card-body">
                            <img style={{margin: "auto", display: "block"}} width={100} src={product.imgUrl} alt="" />
                        </div>
                        <div className="card-footer">
                            <button className='btn btn-success mx-2 my-2'> + </button>
                            {product.amount}
                            <button className='btn btn-success mx-2 my-2'> - </button>
                        </div>
                    </div>)
                    }
                </div>
            </div>
        </div>
    );
}

export default Card;
