import React from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { apiCall } from '../store/slice/apiCall';
import { getCurrentUser } from '../store/slice/userReducer';

function Settings() {

    const {currentUser} = useSelector(state => state.userReducer);
    const dispatch = useDispatch();

    function changePhoto(e){
        
        const formData = new FormData();
        formData.append("file", e.target.files[0]);
        apiCall("/file/edit/"+currentUser.id, "put", formData).then(res=>{
            dispatch(getCurrentUser({value: currentUser.id}))
            console.log("salom", currentUser);
        })
    }

    return (
        <div className='container'>
            <div className="row">
                <div className="col-5">
                    <div className="card">
                        <div className="card-header" style={{position: "relative"}}>
                            <img style={{height: "80px", width: "80px"}} src={"http://localhost:8080/file/"+currentUser.photoUrl} alt="" />
                            <input onChange={changePhoto} id='edit' type="file" style={{display: "none"}} />
                            <label htmlFor="edit">
                                <i style={{fontSize: "30px", marginTop: "50px" , position: "absolute", bottom: "0px"}} className='bi bi-plus'></i>
                            </label>
                        </div>
                        <div className="card-body">
                            <h5>Full name: {currentUser.fullName}</h5>
                            <p>Phone number: {currentUser.phoneNumber}</p>
                        </div>
                        <div className="card-footer">
                            <button className='btn btn-danger my-2 '>log out account</button>
                        </div>
                    </div>
                </div>
            </div>
            
        </div>
    );
}

export default Settings;
